    private function getNCfromAPI () {
        $url = "https://web.spaggiari.eu/services/ws/wsExtAuth.php?wsdl";
        $client = new SoapClient( $url );
        $result = $client->__soapCall("wsExtAuth..ckAuth",
            array(
                'cid' => 'FILS0001',
                'login' => sanitize(INPUT_POST, 'username', FILTER_SANITIZE_FULL_SPECIAL_CHARS),
                'password' => sanitize(INPUT_POST, 'password', FILTER_SANITIZE_FULL_SPECIAL_CHARS)
            )
        );
        if ($result[0] == 'EWS-005') {
            return array('Alunno non appartenente al Castelnuovo.');
        } else if ($result[0] == 'EWS-001' || $result[0] == 'EWS-004') {
            return array('Credenziali errate. Se il problema persiste contatta la segreteria al più presto per richiedere un reset della password.');
        } else if ($result[0] != '') {
            return array('Errore sconosciuto. Se il problema persiste contattare un rappresentante o uno dei ragazzi in Crediti.');
        } else if ($result[2]->anno_scol != CURRENT_YEAR) {
            return array('Studente non attualmente iscritto al Castelnuovo.');
        } else {
            return array('', filter_var($result[2]->nome, FILTER_SANITIZE_FULL_SPECIAL_CHARS), filter_var($result[2]->cognome, FILTER_SANITIZE_FULL_SPECIAL_CHARS), filter_var($result[2]->id, FILTER_SANITIZE_FULL_SPECIAL_CHARS));
        }
    }
